package ru.bmstu.rk9.database;

import java.sql.SQLException;
import java.util.logging.Level;
import java.util.logging.Logger;

/**
 * Created by farid on 4/7/17.
 */
public class TableManager {
    public void createTables() {
        try {
            Database.update("CREATE COLUMN TABLE tracker (" +
                    "id integer GENERATED BY DEFAULT AS IDENTITY," +
                    "created TIMESTAMP," +
                    "device VARCHAR(50)," +
                    "state VARCHAR(10)," +
                    "PRIMARY KEY (id)" +
                    ");");
        } catch (SQLException ex) {
            Logger.getLogger(Logger.class.getName()).log(Level.WARNING, ex.getMessage(), ex);
        }
        try {
            Database.update("CREATE COLUMN TABLE state_logger (" +
                    "id INTEGER GENERATED BY DEFAULT AS IDENTITY," +
                    "created TIMESTAMP," +
                    "machine_1 TINYINT," +
                    "machine_2 TINYINT," +
                    "robot_1 TINYINT," +
                    "robot_2 TINYINT," +
                    "stacker TINYINT," +
                    "key_1 TINYINT," +
                    "key_2 TINYINT," +
                    "key_3 TINYINT," +
                    "key_4 TINYINT," +
                    "collet_1 TINYINT," +
                    "collet_2 TINYINT," +
                    "PRIMARY KEY (id)" +
                    ");");
        } catch (SQLException ex) {
            Logger.getLogger(Logger.class.getName()).log(Level.WARNING, ex.getMessage(), ex);
        }
    }
}
